import React from 'react'
import { Mutation } from 'react-apollo'
import { Button } from '@material-ui/core'
import { GET_CARLIST, REMOVE_CAR } from '../queries'
import { filter } from 'lodash'

const RemoveCar = ({ id, year, make, model, price, ownerId }) => {
  return (
    <Mutation
      mutation={REMOVE_CAR}
      update={(store, { data: { removeCar } }) => {
        const { cars } = store.readQuery({ query: GET_CARLIST })
        store.writeQuery({
          query: GET_CARLIST,
          data: { cars: filter(cars, c => { return c.id !== removeCar.id }) }
        })
      }}
    >
      {removeOwner => (
        <Button onClick={e => {
          e.preventDefault()
          removeOwner({
            variables: {
              id
            },
            optimisticResponse: {
              __typename: 'Mutation',
              removeContact: {
                __typename: 'Owners',
                id,
                firstName,
                lastName
              }
            }
          })
        }}
          variant='contained'
          color='secondary'
          style={{ margin: '5px' }}
        >
          Delete
        </Button>
      )}
    </Mutation>
  )
}

export default RemoveCar
